    private void jBBuscarMaterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBBuscarMaterActionPerformed
         try{
        Integer codigo = Integer.parseInt(jtCodigo.getText());
        materiaActual = matedata.buscarMateria(codigo);// inicializamos la variable
        
        if(materiaActual != null){
            jtNombre.setText(materiaActual.getNombre());
            Integer anio = materiaActual.getAnioMateria();//no sale en el tx
            
            jrbEstado.setSelected(materiaActual.isEstado());
        }
        
        }catch(NumberFormatException ex){
          JOptionPane.showMessageDialog(this, "Debe ingresar Codigo de Materia ");
        }
    }//GEN-LAST:event_jBBuscarMaterActionPerformed

