    private void jbNuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbNuevoActionPerformed
        limpiar();
        materiaActual=null;
    }//GEN-LAST:event_jbNuevoActionPerformed

    private void jBGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBGuardarActionPerformed
        try{
            Integer id = Integer.parseInt(jtCodigo.getText());
            String nombre = jtNombre.getText();
            
            if(nombre.isEmpty()){
                 JOptionPane.showMessageDialog(this, "Ingrese un nombre de la Materia ");
                return;
            }
//            
            int anio = Integer.parseInt(jtAño.getText());
            Boolean estado = jrbEstado.isSelected();
              
              if(materiaActual ==null){
                 materiaActual=new Materia(id,nombre, anio, estado);
                 matedata.guardarMateria(materiaActual);
                 
              }else{
              materiaActual.setIdMateria(id);
              materiaActual.setNombre(nombre);
              materiaActual.setAnioMateria(anio);
//              materiaActual.isEstado();
              matedata.modificarMateria(materiaActual);
              }
        }catch(NumberFormatException ex){
               JOptionPane.showMessageDialog(this, "Debe ingresar un Codigo valido");
        }
    }//GEN-LAST:event_jBGuardarActionPerformed

    private void jbEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbEliminarActionPerformed
        if(materiaActual!=null){
            matedata.eliminarMateria(materiaActual.getIdMateria());
          materiaActual=null;
          limpiar();
        }else{
         JOptionPane.showInternalInputDialog(this, "No hay alumno seleccionado");
        }
        
    
    }//GEN-LAST:event_jbEliminarActionPerformed


